import 'bootstrap/dist/css/bootstrap.min.css'
import { getCookie } from 'typescript-cookie'
import { BasketProduct } from '../components/BasketProduct'

// Basket page
export function BasketPage(){
    let userId = NaN;
    let basketProducts = JSON.parse(localStorage.getItem("Basket") || "{}");

    function BasketInfo(){
        let ProductId: number[] = [];

        if(basketProducts.length !== 0){
            if (Number.isNaN(userId) === false && basketProducts.length !== 0){
    
                // adding product IDs from an object
                for (let i = 0; i < basketProducts.length; i++) {
                    ProductId.push(basketProducts[i].id);            
                };
    
                // Returning cart product cards
                return (
                    <>
                    {ProductId.map(ProductId => 
                        <BasketProduct id = { ProductId } userId = { userId } key = { ProductId } />
                    )} 
                    </>
                );
            } else if(basketProducts.length === 0){
                return (
                    <h4 className='text-center p-5'>Корзина пуста</h4> 
                )
            }
        } else if(getCookie("id") !== undefined){
            return (
                <h4 className='text-center p-5'>Корзина пуста</h4> 
            )
        } else{
            return (
                <h4 className='text-center p-5'>Войдите с помощью VK</h4> 
            )
        }
    }

    
    function GetSum(){
        if (Number.isNaN(userId) === false){
            let sum = 0;
            for (let i = 0; i < basketProducts.length; i++) {
                sum += basketProducts[i].count * basketProducts[i].price;
            }
            return (
            <>
                <h1 className='text-end'>Сумма: {sum} </h1>
                <button className='ButtonEvent'>Заказать</button>
            </>
            )
        }
        else{
            return <></>
        }
    }

    
    return (
        <div className='basket container'>
            <h1>Корзина</h1>
            <hr/>
            <div>
                <BasketInfo/>
            </div>
            <hr/>
            <GetSum/>   
        </div>
    )
}
