import { NavBar } from './components/NavBar'
import AppRoutes from './AppRoutes';
import { Component, ReactNode } from 'react';


class App extends Component{
  componentDidMount(): void {
    // fetch("https://oauth.vk.com/access_token?client_id=51483485&client_secret=7d70138d7d70138d7d70138d777e6180d077d707d70138d1ef0e7bf8114ed0f959527ce&redirect_uri=http://localhost:3000/afterlogin&code=f7s8d7fds897gh",
    // {mode: 'cors'})
    // .then(Response => console.log(Response.json));

    fetch('https://localhost:4000/UserApi/Login', {
                method: 'post',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    Login: this.state.Login,
                    Password: this.state.Password
                })
            }).then((Response) => Response.json())
                .then((result) => {
                    console.log(result);
                    if (result["message"] !== "Success") {
                        this.toggle();
                    }
                    else {
                        window.location.assign('https://localhost:44450');
                        localStorage.setItem('authenticated', true);
                    }
                })

    // fetch("http://localhost:4000/")
    // .then(response => console.log(response))
  }

  render(): ReactNode {
    return (
        <div className='font'>
          <NavBar/>
          <AppRoutes/>
        </div>
      )
  }
}

export default App;
