import { NavBar } from './components/NavBar'
import AppRoutes from './AppRoutes'
import { ReactNode } from 'react'
import { getCookie, setCookie} from 'typescript-cookie'


class App extends Component{
  async componentDidMount() {
    if(getCookie("islogin") === "true") {
      console.log("auth")
    } else{
      setCookie('islogin', false);
      let code = (window.location.href).substring(28);
      const resp = await fetch("http://localhost:3001/login?code="+code);
      const data = await resp.json();  
      if ( data["message"] === "error"){
        console.log(data);
      } else {
        const islogin = true;
        const id = await data["id"];
        const message = await data["crop_photo"]["photo"]["sizes"][1]["url"];
        setCookie('islogin', islogin);
        setCookie('id', id);
        setCookie('image', message);
        window.location.reload();
      }
    }
  }


  render(): ReactNode {
    return (
        <div className='font'>
          <NavBar/>
          <AppRoutes/>
        </div>
      )
  }
}

export default App;
