import 'bootstrap/dist/css/bootstrap.min.css'
import { getCookie } from 'typescript-cookie'
import { BasketProduct } from '../components/BasketProduct'
import { basketProducts } from '../data/basketProducts'

// Basket page
export function BasketPage(){
    let userId = Number(getCookie("id"))
    getUserBasketProduct = () => {
        for(let i = 0; i < basketProducts.length; i++){
            if( basketProducts[i].userId === Number(userId)){
                userId = i;
                break;
            }
        }       
        for(let i = 0; i < basketProducts[userId].productInfo.length; i++){
            return (
                <div>
                    <BasketProduct id={i} userId={userId} key={i}/>)
                </div>
            ) 
        }
    }
    
    return (
        <div className='basket container'>
            <h1>Корзина</h1>
            <hr/>
            <div>
                <getUserBasketProduct/>
            </div>
            <hr/>
            <h1 className='text-end'>Сумма: </h1>
            <button className='ButtonEvent' onClick={ getUserBasketProduct }>Заказать</button>
        </div>
    )
}
