import React from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { products } from "../data/products";


interface CheckInfoProductProps {
    onCheck: () => void
    id: number
}

interface GetIdProduct {
    id: number
}

export function InfoProduct({onCheck}: CheckInfoProductProps, {id}: GetIdProduct){

    const closeHandler = (event: React.MouseEvent) => {
        event.preventDefault()
        onCheck()
    }
    
    return (
        <div className="bg-dark text-white modals">
            <img src={products[id].imageMain} alt="ProductImage" width={650} />
            <p>title: {products[id].title}</p>
            <p>Price: {products[id].price}</p>
            <p>Decription: {products[id].description}</p>
            <button onClick={closeHandler}>Buy</button>
        </div>
    )
}
