import 'bootstrap/dist/css/bootstrap.min.css'
import { getCookie } from 'typescript-cookie'
import { BasketProduct } from '../components/BasketProduct'
import { basketProducts } from '../data/basketProducts'

// Basket page
export function BasketPage(){

    let userId = NaN;
    function BasketInfo(){
        let ProductId: number[] = [];

        // search for a user ID in the product basket object by VK ID
        for(let i = 0; i < basketProducts.length; i++){
            if( basketProducts[i].userId === Number(getCookie("id"))){
                userId = i;
            }
        }
        console.log(Number.isNaN(userId));
        if (Number.isNaN(userId) === false){

            // // adding product IDs from an object
            // for (let i = 0; i < basketProducts[Number(userId)].productInfo.length; i++) {
            //     ProductId.push(basketProducts[Number(userId)].productInfo[i].id);            
            // };

            // // Returning cart product cards
            // return (
            //     <>
            //     {ProductId.map(ProductId => 
            //         <BasketProduct id = { ProductId } userId = { userId } key = { ProductId } />
            //     )} 
            //     </>
            // );

            return (
                <h1>Корзина</h1>
            )
        } else{
            return (
                <h1>Корзина пуста</h1> 
            )
        }
    }

    function GetSum(){
        if (Number.isNaN(userId) === false){
            let sum = 0;
            for (let i = 0; i < basketProducts[Number(userId)].productInfo.length; i++) {
                sum += basketProducts[Number(userId)].productInfo[i].count * basketProducts[Number(userId)].productInfo[i].price;
                console.log(sum);             
            }
            return (
            <>
            <h1 className='text-end'>Сумма: {sum} </h1>
            </>
            )
        }
        else{
            return  <p>s</p>
        }
    }

    
    return (
        <div className='basket container'>
            <h1>Корзина</h1>
            <hr/>
            <div>
                <BasketInfo/>
            </div>
            <hr/>
            <div>
                { GetSum }
            </div>
            <button className='ButtonEvent'>Заказать</button>
        </div>
    )
}
