import { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import { products } from '../data/products';    
import { getCookie } from 'typescript-cookie';
import { BasketProduct } from '../components/BasketProduct';
import { basketProducts } from '../data/basketProducts';

interface IProps{
}
interface ISumState{
    sum: Number;
}

export class BasketPage extends Component<IProps,ISumState> {
    constructor(props: IProps) {
        super(props);
        this.state = {
            sum: 0
        }
        this.setSum = this.setSum.bind(this);
    }

    BasketProduct(){
        return ( 
            <>
            { basketProducts.map(BasketProduct => <BasketProduct basketProduct={BasketProduct} key={BasketProduct.userId}/>) }
            </>
        )
        // }
    }
    
    render() {
        return (
            <div className='basket container'>
                <h1>Корзина</h1>
                <hr/>
                <this.BasketProduct/>
                <hr/>
                <h1 className='text-end'>Сумма: </h1>
                <button className='ButtonEvent' >Заказать</button>
            </div>
        )
        }
}
