import { IBasketProduct } from "../models";
import { products } from "../data/products";

interface CheckBasketProductProps {
    id: number
}

interface BasketProductProps{
    BasketProduct: IBasketProduct
}

// card basket product
export function BasketProduct({ BasketProduct }: BasketProductProps, { id }: CheckBasketProductProps){
    const product = BasketProduct.productInfo;
    
    return( 
        <div className="basketItems">
            <div className="d-flex flex-row">
                <div className="p-2">
                    <img src={products[id].imageSecond} width={150} height={150} alt="product" />
                </div>
                <div className="p-2">
                    <h1> { product[id].title } </h1>
                    <p> {products[id].category} </p>
                </div>
            </div>
            <div>
                <div>
                    <h1>{product[id].price * product[id].count} ₽</h1>
                </div>
                <div className="d-flex">
                    <p className="text-end">кол-во - {product[id].count} |</p>
                    <button className="ButtonValue">+</button>
                    <button className="ButtonValue">-</button>
                </div>
                <div>
                    <button className="ButtonEvent" >Убрать</button>
                </div>
            </div>
        </div>
    );
}
