import 'bootstrap/dist/css/bootstrap.min.css'
import { Navbar, NavbarBrand, NavItem, NavLink } from 'reactstrap'
import { Link } from 'react-router-dom'

export function NavBar(){

    function loginHandler(){
        // const top = window.outerHeight / 2 + window.screenY - 350 / 2;
	    // const left = window.outerWidth / 2 + window.screenX - 420 / 2;
        // return window.open(
        //     "https://oauth.vk.com/authorize?client_id=51483485&redirect_uri=http://localhost:3000/afterlogin&scope=1121&display=popup&response_type=code",
        //     'VkAuth',
        //     `height=${350},width=${420},top=${top},left=${left}`
        // );


        window.location.href = "https://oauth.vk.com/authorize?client_id=51483485&redirect_uri=http://localhost:3000/afterlogin&scope=1121&display=popup&response_type=code";
    }
    
    return (
    <>                      
        <header>
            <Navbar className="navbar-expand-lg fixed-top navbarBg font" container light>
                <NavbarBrand tag={Link} to="/" className="text-white"><svg className='Favicon' width={100} height={100}/></NavbarBrand>
                <div className="flex-grow-1">
                    <ul className="navbar-nav mr-auto mt-2 mt-lg-0">
                        <NavItem>
                            <NavLink tag={Link} className="text-white" to="/">Магазин</NavLink>
                        </NavItem>
                        <NavItem>
                            <NavLink tag={Link} className="text-white" to="/product">Продукты</NavLink>
                        </NavItem>
                    </ul>
                </div>
                <div className="flex-grow-2">
                    <ul className="navbar-nav mr-auto mt-2 mt-lg-0">
                        <NavItem>
                            <NavLink tag={Link} className="text-white" to="/basket">Корзина</NavLink>
                        </NavItem>
                    </ul>
                </div>
                <div className="flex-grow-2 pleft">
                    <ul className="navbar-nav mr-auto mt-2 mt-lg-0">
                        <NavItem >   
                            <NavLink tag={Link} to="/"><img className='avatar' height={50} width={50} src="" alt="" /></NavLink>
                        </NavItem>
                    </ul>
                </div>  
                <div className="flex-grow-2 pleft">
                    <ul className="navbar-nav mr-auto mt-2 mt-lg-0">
                        <NavItem >   
                            <button onClick={loginHandler}>Войти</button>
                        </NavItem>
                    </ul>
                </div>  
            </Navbar>
        </header>
        </>
        )
}
