import { IProduct } from '../models'
import {ModalContext} from '../context/ModalContext'
import {Modal} from '../components/Modal'
import {InfoProduct} from '../components/InfoProduct'
import { create } from 'domain'

interface ProductProps{
    product: IProduct
}

export function Product({product}: ProductProps){
    const {modal, setModal} = useState(true)

    const closeHandler = (product: IProduct) => {
        close()
    }

    return (
        <div className='d-flex flex-column p-3'>
            <div className='d-flex  justify-content-center m-0'>
                <img width={350} height={350} src={product.imageSecond} alt="" onClick={open}/>
            </div>
            <h4 className='text-center m-0'>{product.title}</h4>
            <p className='text-center m-0'>{product.price} â‚½</p>
            <button onClick={open}></button>

            {modal && <Modal title="Info product" onClose={close}>
                <InfoProduct onCheck={closeHandler}/>
            </Modal>} 
        </div>


    )
}
