import React from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { products } from "../data/products";


interface CheckInfoProductProps {
    onCheck: () => void
    id: number
}


export function InfoProduct({onCheck}: CheckInfoProductProps, {id}: CheckInfoProductProps){

    const closeHandler = (event: React.MouseEvent) => {
        event.preventDefault()
        onCheck()
    }
    
    return (
        <div className="bg-dark text-white modals">
            <p>{products[id].description}</p>
            <button onClick={closeHandler}>Buy</button>
        </div>
    )
}
