import 'bootstrap/dist/css/bootstrap.min.css';
import { BasketProduct } from '../components/BasketProduct';
import { products } from '../data/products';    
import { Component } from 'react'
import { getCookie } from 'typescript-cookie';
import { basketProducts } from '../data/basketProduct';

interface IProps{
}
interface ISumState{
    sum: Number;
}

export class BasketPage extends Component<IProps,ISumState> {
    constructor(props: IProps) {
        super(props);
        this.state = {
            sum: 0
        }
        this.setSum = this.setSum.bind(this);
    }

    componentDidMount() {
        this.setSum();
    }

    // Item basket product 
    setSum(){
        let price = 0;
        for (let i = 0; i < ("" + getCookie("ProductId")).length; i++) {
            const product = ("" + getCookie("ProductId"))[i];
            price += products[Number(product)].price;
        }
        
        this.setState((previousState, props) => ({
            sum: price
        }));
    }

    BasketProduct(){
        return ( 
            <>
            </>
        )
        // }
    }
    
    render() {
        return (
            <div className='basket container'>
                <h1>Корзина</h1>
                <hr/>
                <this.BasketProduct/>
                <hr/>
                <h1 className='text-end'>Сумма: { String(this.state.sum) }</h1>
                <button className='ButtonEvent' >Заказать</button>
            </div>
        )
        }
}
