import 'bootstrap/dist/css/bootstrap.min.css';
import { BasketProduct } from '../components/BasketProduct';
import { products } from '../data/products';    
import { Component, ReactNode, useState } from 'react'
import { getCookie } from 'typescript-cookie';

interface IProps{
}
interface ISumState{
    sum: Number;
}

export class BasketPage extends Component<IProps,ISumState> {
    constructor(props: IProps) {
        super(props);

        this.state = {
            sum: 0
        }
        this.SetSum = this.SetSum.bind(this);
    }

    SetSum(){
        this.setState((previousState, props) => ({
            sum: 3,
        }));
    }

    // Item basket product 
    BasketProducts = (
        function(){
            const ProductId: string[] = [];
            for(var i = 0; i <= ("" + getCookie("ProductId")).length-1; i++){
                ProductId.push(("" + getCookie("ProductId"))[i]);
            }
            if (getCookie("ProductId") === ""){
                return (
                    <>
                    <p className='text-center p-5'>Корзина пуста</p>
                    </>
                )
            }
            if(typeof(getCookie("ProductId")) == typeof(undefined)){
                return(
                    <>
                    <p className='text-center p-5'>Войдите через VK</p>
                    </>
                )
            } 
            else{
                setState((previousState, props) => ({
                    sum: 3,
                }));
                return ( 
                    <>
                    {ProductId.map(ProductId => 
                        <BasketProduct id={Number(ProductId)} key={ Number(ProductId)}/>
                    )}
                    </>
                )
            }
        }
    );
    
    render() {
        return (
            <> 
            <div className='basket container'>
                <h1>Корзина</h1>
                <hr/>
                <this.BasketProducts/>
                <hr/>
                <h1 className='text-end'>Сумма: { String(this.state.sum) }</h1>
                <button className='ButtonEvent' onClick={ this.SetSum }>Заказать</button>
            </div>
            </>
        )
        }
}
