import React, { useState } from 'react'
import { basketProducts } from "../data/basketProducts"
import { products } from "../data/products"

interface CheckBasketProductProps {
    id: number
    userId: number
}

// card basket product
export function BasketProduct({ id, userId }: CheckBasketProductProps){
    let [count, setCount] = useState(NaN);

    // search count by product ID
    for(let i = 0; i < basketProducts[userId].productInfo.length; i++){
        if( basketProducts[userId].productInfo[i].id === id){
            count = basketProducts[userId].productInfo[i].count;
        }
    }

    const plusCount = () => {
        for(let i = 0; i < basketProducts[userId].productInfo.length; i++){
            if( basketProducts[userId].productInfo[i].id === id){
                basketProducts[userId].productInfo[i].count +=  1;
                count +=  1;
                console.log(basketProducts[userId].productInfo[i].count);
            }
        }
        window.location.reload();
    }
    
    const minusCount = () => {
        for(let i = 0; i < basketProducts[userId].productInfo.length; i++){
            if( basketProducts[userId].productInfo[i].id === id){
                basketProducts[userId].productInfo[i].count -= 1;
                count -= 1;
                console.log(basketProducts[userId].productInfo[i].count);
            }
        }
    }

    return( 
        <div className="basketItems">
            <div className="d-flex flex-row">
                <div className="p-2">
                    <img src={ products[id].imageSecond }  width={150} height={150} alt="product" />
                </div>
                <div className="p-2">
                    <h1> { products[id].title } </h1>
                    <p> { products[id].category } </p>
                </div>
            </div>
            <div>
                <div>
                    <h1>{ products[id].price } ₽</h1>
                </div>
                <div className="d-flex">
                    <p className="text-end">кол-во - { count } |</p>
                    <button className="ButtonValue" onClick={ plusCount }>+</button>
                    <button className="ButtonValue" onClick={ minusCount }>-</button>
                </div>
                <div>
                    <button className="ButtonEvent" >Убрать</button>
                </div>
            </div>
        </div>
    );
}
